mapper.select:
SELECT * FROM mem_baseinfo mb


        and mb.id = #{dto.id}


        and mb.mem_level = #{dto.memLevel}


        and mb.group_id = #{dto.groupId}


        and mb.status = #{dto.status}


        and mb.real_name like concat('%', #{dto.realName,jdbcType=VARCHAR}, '%')


        and mb.r_invite_code = #{dto.rInviteCode}


        and date_format(mb.create_time,'%y%m%d') >= date_format(#{dto.regStartTime},'%y%m%d')


        and date_format(mb.create_time,'%y%m%d') <= date_format(#{dto.regEndTime},'%y%m%d')


ORDER BY mb.create_time desc
