mapper.select:
SELECT * FROM mem_bank_relation mbr
where 1=1


        and mbr.mem_id = #{req.keyword}


        and mbr.bank_card_no like concat('%', #{req.keyword,jdbcType=VARCHAR}, '%')


        and mbr.city like concat('%', #{req.keyword,jdbcType=VARCHAR}, '%')


        and mbr.bank_name like concat('%', #{req.keyword,jdbcType=VARCHAR}, '%')




    and mbr.create_time >= #{req.startTime}


    and mbr.create_time <= #{req.endTime}


ORDER BY mbr.create_time desc
