mapper.select:
SELECT mb.id, mb.account, mb.acc_type AS accType, mb.mem_level AS memLevel, mb.phone, mb.email, mb.face_book AS faceBook, mb.whats_app AS whatsApp, mb.balance, mb.total_deposit AS totalDeposit, mb.total_enchashment AS totalEnchashment, mb.total_bet AS totalBet, mb.deposit_num AS depositNum, mb.take_cash_num AS takeCashNum, mb.`status`, mb.register_source AS registerSource, mb.prohibit_login AS prohibitLogin, mb.prohibit_invite AS prohibitInvite, mb.prohibit_investment AS prohibitInvestment, mb.prohibit_disbursement AS prohibitDisbursement, mb.prohibit_recharge AS prohibitRecharge, mb.first_recharge_time AS firstRechargeTime, mb.last_login_time AS lastLoginTime, mb.create_time AS createTime, mic.invite_code AS inviteCode, ar.team_num AS teamNum FROM mem_baseinfo mb LEFT JOIN mem_invite_code mic ON mb.id = mic.mem_id LEFT JOIN agent_relation ar ON mb.id = ar.mem_id
and mb.account = #{dto.account}
and mb.mem_level = #{dto.memLevel}
and mb.status = #{dto.status}
and mb.real_name like concat('%', #{dto.realName,jdbcType=VARCHAR}, '%')
and mb.invite_code = #{dto.inviteCode}
and date_format(mb.create_time,'%y%m%d') >= date_format(#{dto.startTime},'%y%m%d')
and date_format(mb.create_time,'%y%m%d') <= date_format(#{dto.endTime},'%y%m%d')
ORDER BY mb.create_time desc

SELECT id
FROM mem_baseinfo
where DATE_FORMART(create_time, "%Y-%m-%d") = #{date}

select

from mem_baseinfo mb
where mb.account = #{account}
