mapper.select:
SELECT date_format(etime, '%Y-%m-%d') AS date,
       SUM(abs(allbet))                    AS gameamt,
       SUM(profit)                    AS gameawardamt
FROM ae_bet_order
WHERE etime BETWEEN #{startTime} and #{endTime}
GROUP BY date;

SELECT user_id as userid,
       SUM(abs(allbet)) AS gameamt,
       SUM(profit) AS gameawardamt
FROM ae_bet_order
WHERE etime BETWEEN #{startTime} and #{endTime}
GROUP BY user_id;
