mapper.select:
SELECT date_format(game_date, '%Y-%m-%d') AS date,
       SUM(ABS(bet))                      AS gameamt,
       SUM(total)                           AS gameawardamt
FROM db_bet_order
WHERE game_date BETWEEN #{startTime} and #{endTime}
GROUP BY date;

SELECT member_id     as userid,
       SUM(ABS(bet)) AS gameamt,
       SUM(total)      AS gameawardamt
FROM db_bet_order
WHERE game_date BETWEEN #{startTime} and #{endTime}
GROUP BY member_id;
