mapper.select:
SELECT date_format(game_end_time, '%Y-%m-%d') AS date,
       SUM(abs(all_bet))                           AS gameamt,
       SUM(profit)                            AS gameawardamt
FROM ky_bet_order
WHERE game_end_time BETWEEN #{startTime} and #{endTime}
GROUP BY date;

SELECT user_id AS userid,
       SUM(abs(all_bet))                           AS gameamt,
       SUM(profit)                            AS gameawardamt
FROM ky_bet_order
WHERE game_end_time BETWEEN #{startTime} and #{endTime}
GROUP BY user_id;
