mapper.select:
SELECT mb.id AS memId, mb.account AS account, mb.mem_level AS memLevel, mb.real_name AS realName, mb.acc_type AS accType, mb.total_deposit AS totalDeposit, mb.total_enchashment AS totalEnchashment, mb.total_bet AS totalBet, mb.create_time AS registerTime, ma.agent_id AS agent_id, ma.superior AS superior, ma.team_num AS teamNum FROM agent_relation ma INNER JOIN mem_baseinfo mb ON ma.mem_id = mb.id and ma.superior = #{req.superior}
and ma.account = #{req.subAccount}
ORDER BY ma.create_time DESC
